$(document).scroll(function () {
    if ($(document).scrollTop() >= 300) {
        $('.top').addClass('solid');
    } else {
        $('.top').removeClass('solid');
    }
});

var x=0;
$('.pull-right.btn').click(function (e) {
    e.preventDefault();
    if (x==0) {
        x=1
        $('.pull-right.btn>div').addClass('active');
        $('#drawer-right').css({
            'margin-right': '0'
        })
        $('#wrapper').css({
            'margin-left': '-300px'
        });
    }else{
        x=0
        $('.pull-right.btn>div').removeClass('active');
        $('#drawer-right').css({
            'margin-right': '-300px'
        })
        $('#wrapper').css({
            'margin-left': '0'
        });
    }
});
$('.cross a').click(function (e) { 
    e.preventDefault();
    $('.pull-right.btn>div').removeClass('active');
    $('#drawer-right').css({
        'margin-right': '-300px'
    })
    $('#wrapper').css({
        'margin-left': '0'
    });
});

function showpic(e) {
    $('.header-wrapper-inner').eq(e).addClass('active').siblings().removeClass('active')
    $('#header ul.controldot li').eq(e).addClass('active').siblings().removeClass('active')
}

function currentIndex() {
    return ($('.header-wrapper-inner.active').index())
}

function nextpic() {
    var nextindex = currentIndex() + 1
    if (nextindex == 3) {
        nextindex = 0
    }
    showpic(nextindex)
}

function prevpic() {
    var previndex = currentIndex() - 1
    if (previndex == -1) {
        previndex = 2
    }
    showpic(previndex)
}

var lunbo_time = setInterval(function () {
    var current = currentIndex()
    current++
    if (current == 3) {
        current = 0
    }
    showpic(current)
}, 3000)

$('.flex-direction-nav li').hover(function () {
    clearInterval(lunbo_time)
}, function () {
    lunbo_time = setInterval(function () {
        var current = currentIndex()
        current++
        if (current == 3) {
            current = 0
        }
        showpic(current)
    }, 3000)
});
$('#header ul.controldot li').hover(function () {
    clearInterval(lunbo_time)
}, function () {
    lunbo_time = setInterval(function () {
        var current = currentIndex()
        current++
        if (current == 3) {
            current = 0
        }
        showpic(current)
    }, 3000)
});

$('.flex-direction-nav .next').click(function () {
    nextpic()
});
$('.flex-direction-nav .prev').click(function () {
    prevpic()
});
$('#header ul.controldot li').click(function () {
    showpic($(this).index())
});

$('.photobox').hover(function () {
    $(this).addClass('active')
}, function () {
    $(this).removeClass('active')
});

$('.clickpage ul li').click(function () {
    $(this).addClass('active').siblings().removeClass('active')
    if ($(this).index() == 0) {
        $('#one')[0].src = "./imgs/bg1.jpg"
        $('#two')[0].src = "./imgs/bg3.jpg"
        $('#three')[0].src = "./imgs/bg4.jpg"
        $('#four')[0].src = "./imgs/bg5.jpg"
    } else if ($(this).index() == 1) {
        $('#one')[0].src = "./imgs/bg6.jpg"
        $('#two')[0].src = "./imgs/bg7.jpg"
        $('#three')[0].src = "./imgs/bg8.jpg"
        $('#four')[0].src = "./imgs/bg_xk.jpg"
    } else if ($(this).index() == 2) {
        $('#one')[0].src = "./imgs/bg3.jpg"
        $('#two')[0].src = "./imgs/bg1.jpg"
        $('#three')[0].src = "./imgs/bg5.jpg"
        $('#four')[0].src = "./imgs/bg8.jpg"
    }
});